import React, { useRef } from "react";
import "./Todos.css";
import TodoItem from "./TodoItem";

//할일들
const Todos = ({
  input, //입력된 텍스트
  todos, //할일 목록
  onChangeInput,
  onInsert,
  onToggle,
  onRemove,
  onEdit,
  loadingTodos, //로딩
}) => {
  const onSubmit = (e) => {
    e.preventDefault();

    if (input == "") {
      alert("공백은 추가할 수 없습니다.");
    } else {
      onInsert(input);
    }
    onChangeInput("");
  };
  const onChange = (e) => onChangeInput(e.target.value);
  return (
    <>
      <form className="form" onSubmit={onSubmit}>
        <textarea
          value={input}
          onChange={onChange}
          placeholder="할 일을 입력하세요."
        />
        <button className="createBtn" type="submit">
          추가
        </button>
      </form>
      <div className="todoList" onDragStart="return false">
        {loadingTodos && null}
        {!loadingTodos && todos && (
          <div>
            {todos.map((todo, index) => (
              <TodoItem
                todo={todo}
                key={index}
                onToggle={onToggle}
                onRemove={onRemove}
                onEdit={onEdit}
              />
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default Todos;
