import { handleActions } from "redux-actions";
import * as todoApi from "../lib/api/todoApi";
import createRequestThunk from "../lib/createRequestThunk";

//액션타입 정의하기
const INSERT_COLOR = "color/INSERT_COLOR";
const INSERT_COLOR_SUCCESS = "color/INSERT_COLOR";

const GET_COLOR = "color/GET_COLOR";
const GET_COLOR_SUCCESS = "color/GET_COLOR_SUCCESS";

//액션 생성함수 만들기
export const insertColor = crea;
export const getColor = createRequestThunk(GET_COLOR, todoApi.getTodos);

const initialState = {
  colorId: 1,
};

//리듀서함수 만들기
const todos = handleActions(
  {
    [INSERT_COLOR_SUCCESS]: (state, action) => ({
      ...state,
      colorId: action.payload,
    }),
    [GET_COLOR_SUCCESS]: (state, action) => ({
      ...state,
      colorId: action.payload,
    }),
  },
  initialState
);

export default color;
