import * as api from "../lib/api";
import createRequestThunk from "../lib/createRequestThunk";

const GET_TODOS = "apiRequest/GET_TODOS";
const GET_TODOS_SUCCESS = "apiRequest/GET_TODOS_SUCCESS";

const INSERT_TODO = "apiRequest/INSERT_TODO";
const INSERT_TODO_SUCCESS = "apiRequest/INSERT_TODO_SUCCESS";

const UPDATE_TODO = "apiRequest/UPDATE_TODO";
const UPDATE_TODO_SUCCESS = "apiRequest/UPDATE_TODO_SUCCESS";

const DELETE_TODO = "apiRequest/DELETE_TODO";
const DELETE_TODO_SUCCESS = "apiRequest/DELETE_TODO_SUCCESS";

export const getTodos = createRequestThunk(GET_TODOS, api.getTodos);
export const insertTodo = createRequestThunk(GET_TODOS, api.insertTodos);
export const updateTodo = createRequestThunk(GET_TODOS, api.updateTodos);
export const deleteTodo = createRequestThunk(GET_TODOS, api.deleteTodos);

const initialState = {};

export default apiRequest;
