import React from "react";
import "./ColorPick.css";

const ColorPick = ({ color, onClick }) => {
  const onChangeColor = (colorID) => {
    const selectedColor = 1;
    if (colorID === 1) {
      selectedColor = document.documentElement.style.getPropertyValue(
        "--blueColor"
      );
    } else if (colorID === 2) {
      selectedColor = document.documentElement.style.getPropertyValue(
        "--blueColor"
      );
    }

    document.documentElement.style.setProperty(
      "--todoBackgroundColor",
      selectedColor
    );
    const clickfn = async () => {
      try {
        const response = await onClick(colorID);
        console.log(response);
      } catch (e) {
        console.log(e);
      }
    };
    clickfn();
  };
  return (
    <table className="ColorTable">
      <tbody>
        <tr>
          <td>
            <div
              className="blueTemplate"
              onClick={(e) => onChangeColor(1)}
            ></div>
          </td>
          <td>
            <div
              className="purpleTemplate"
              onClick={(e) => onChangeColor(2)}
            ></div>
          </td>
          <td>
            <div
              className="greenTemplate"
              onClick={(e) => onChangeColor(3)}
            ></div>
          </td>
          <td>
            <div
              className="grayTemplate"
              onClick={(e) => onChangeColor(4)}
            ></div>
          </td>
        </tr>
      </tbody>
    </table>
  );
};

export default ColorPick;
