import { createAction, handleActions } from "redux-actions";
import produce from "immer";
import { insertTodo, updateTodo, deleteTodo } from "../lib/api";
import * as api from "../lib/api";
import createRequestThunk from "../lib/createRequestThunk";

//액션타입 정의하기
const CHANGE_INPUT = "todos/CHANGE_INPUT"; //인풋 값을 변경
const INSERT = "todos/INSERT"; //todo 등록
const TOGGLE = "todos/TOGGLE"; //todo 체크
const REMOVE = "todos/REMOVE"; //todo제거
const EDIT = "todos/Edit"; //todo수정

const GET_TODOS = "todos/GET_TODOS";
const GET_TODOS_SUCCESS = "todos/GET_TODOS_SUCCESS";

//액션 생성함수 만들기
export const changeInput = createAction(CHANGE_INPUT, (input) => input);

let id = 1;
export const insert = createAction(INSERT, (text) => ({
  id: id++,
  text,
  checked: false,
}));

export const toggle = createAction(TOGGLE, (id) => id);

export const remove = createAction(REMOVE, (id) => id);

export const edit = createAction(EDIT, (id) => id);

export const getTodos = createRequestThunk(GET_TODOS, api.getTodos);

const initialState = {
  input: "",
  todos: [],
};

//리듀서함수 만들기 (immer사용)
const todosa = handleActions(
  {
    [CHANGE_INPUT]: (state, { payload: input }) =>
      produce(state, (draft) => {
        draft.input = input;
      }),
    [INSERT]: (state, { payload: todo }) =>
      produce(state, (draft) => {
        draft.todos.push(todo);
        insertTodo(todo);
      }),
    [TOGGLE]: (state, { payload: id }) =>
      produce(state, (draft) => {
        const todo = draft.todos.find((todo) => todo.id === id);
        todo.checked = !todo.checked;
        updateTodo(todo);
      }),
    [REMOVE]: (state, { payload: id }) =>
      produce(state, (draft) => {
        const index = draft.todos.findIndex((todo) => todo.id === id);
        draft.todos.splice(index, 1);
        deleteTodo(id);
      }),
    [EDIT]: (state, { payload: id }) =>
      produce(state, (draft) => {
        const todo = draft.todos.find((todo) => todo.id === id);
        let newtext = window.prompt("ToDo 수정", todo.text);
        if (newtext != null) todo.text = newtext;
        updateTodo(todo);
      }),
    [GET_TODOS_SUCCESS]: (state, action) => ({
      ...state,
      todos: action.payload,
    }),
  },
  initialState
);
console.log(todos);

export default todosa;
