import React, { useRef } from "react";
import { IoMdCheckmark, IoMdClose } from "react-icons/io";
import { BiEditAlt } from "react-icons/bi";

//할일 하나
const TodoItem = ({ todo, onToggle, onRemove, onEdit }) => {
  return (
    <div
      key={todo.id}
      className="listItem"
      onDoubleClick={() => onEdit(todo.id)}
    >
      {todo.checked ? (
        <div className="todoItemChecked">{todo.text}</div>
      ) : (
        <div className="todoItem">{todo.text}</div>
      )}
      <IoMdCheckmark
        size="30"
        className="checkBtn"
        onClick={() => onToggle(todo.id)}
      />
      <BiEditAlt
        size="30"
        className="editBtn"
        onClick={() => onEdit(todo.id)}
      />
      <IoMdClose
        size="30"
        className="deleteBtn"
        onClick={() => onRemove(todo.id)}
      />
    </div>
  );
};

//할일들
const Todos = ({
  input, //입력된 텍스트
  todos, //할일 목록
  onChangeInput,
  onInsert,
  onToggle,
  onRemove,
  onEdit,
}) => {
  const inputEl = useRef(null);
  const onSubmit = (e) => {
    e.preventDefault();
    onInsert(input);
    //onChangeInput("");
    inputEl.current.focus();
  };
  //const onChange = (e) => onChangeInput(e.target.value);
  return (
    <>
      <form className="form" onSubmit={onSubmit}>
        <textarea value={input} onChange={onChange} ref={inputEl} />
        <button className="createBtn" type="submit">
          추가
        </button>
      </form>
      <div className="todoList">
        {todos.map((todo) => (
          <TodoItem
            todo={todo}
            key={todo.key}
            onToggle={onToggle}
            onRemove={onRemove}
            onEdit={onEdit}
          />
        ))}
      </div>
    </>
  );
};

export default Todos;
