import React, { useRef } from "react";
import "./Todos.css";
import TodoItem from "./TodoItem";

//할일들
const Todos = ({
  input, //입력된 텍스트
  todos, //할일 목록
  onChangeInput,
  onInsert,
  onToggle,
  onRemove,
  onEdit,
}) => {
  const inputEl = useRef(null);
  const onSubmit = (e) => {
    e.preventDefault();
    onInsert(input);
    onChangeInput("");
    inputEl.current.focus();
  };
  const onChange = (e) => onChangeInput(e.target.value);
  return (
    <>
      <form className="form" onSubmit={onSubmit}>
        <textarea value={input} onChange={onChange} ref={inputEl} />
        <button className="createBtn" type="submit">
          추가
        </button>
      </form>
      <div className="todoList">
        {todos.map((todo) => (
          <>
            <div key={todo.id} />
            <TodoItem
              todo={todo}
              key={todo.key}
              onToggle={onToggle}
              onRemove={onRemove}
              onEdit={onEdit}
            />
          </>
        ))}
      </div>
    </>
  );
};

export default Todos;
