import React from "react";
import { useSelector } from "react-redux";
import { changeInput, insert, toggle, remove, edit } from "../modules/todos";
import Todos from "../components/Todos";
import { useActions } from "../lib/useAction";
import { useEffect } from "react";
import { getTodos } from "../modules/apiRequest";

const TodosContainer = () => {
  const { input, todos } = useSelector(({ todos }) => ({
    input: todos.input,
    todos: todos.todos,
  }));

  const [onChangeInput, onInsert, onToggle, onRemove, onEdit] = useActions(
    [changeInput, insert, toggle, remove, edit],
    []
  );

  useEffect(() => {
    getTodos();
  });
  return (
    <Todos
      input={input}
      todos={todos}
      onChangeInput={onChangeInput}
      onInsert={onInsert}
      onToggle={onToggle}
      onRemove={onRemove}
      onEdit={onEdit}
    />
  );
};

export default React.memo(TodosContainer);
