import React from "react";
import { useSelector, connect } from "react-redux";
import {
  changeInput,
  insert,
  toggle,
  remove,
  edit,
  getTodos,
} from "../modules/todos";
import Todos from "../components/Todos";
import { useActions } from "../lib/useAction";
import { useEffect } from "react";
//import { getTodos } from "../lib/api";
// import { getTodos } from "../modules/apiModule";

const TodosContainer = ({
  input,
  todos,
  changeInput,
  insert,
  toggle,
  remove,
  edit,
  loadingTodos,
}) => {
  // useEffect(() => {
  //   const fn = async () => {
  //     try {
  //       await getTodos();
  //     } catch (e) {
  //       console.log(e);
  //     }
  //   };
  //   fn();
  // }, [getTodos]);
  useEffect(() => {
    getTodos();
  }, [getTodos]);
  return (
    <Todos
      input={input}
      todos={todos}
      onChangeInput={changeInput}
      onInsert={insert}
      onToggle={toggle}
      onRemove={remove}
      onEdit={edit}
      loadingTodos={loadingTodos}
    />
  );
};

export default connect(
  ({ todos, loading }) => ({
    input: todos.input,
    todos: todos.todos,
    loadingTodos: loading["todos/GET_TODOS"],
  }),
  {
    changeInput,
    insert,
    toggle,
    remove,
    edit,
    getTodos,
  }
)(TodosContainer);
