import React from "react";
import "./ColorPick.css";

const ColorPick = ({ color, onClick }) => {
  var selectedColor = null;
  var selectedBackColor = null;
  if (color === 1) {
    selectedColor = getComputedStyle(document.documentElement).getPropertyValue(
      "--blueColor"
    );
    selectedBackColor = getComputedStyle(
      document.documentElement
    ).getPropertyValue("--blueBackColor");
  } else if (color === 2) {
    selectedColor = getComputedStyle(document.documentElement).getPropertyValue(
      "--purpleColor"
    );
    selectedBackColor = getComputedStyle(
      document.documentElement
    ).getPropertyValue("--purpleBackColor");
  } else if (color === 3) {
    selectedColor = getComputedStyle(document.documentElement).getPropertyValue(
      "--greenColor"
    );
    selectedBackColor = getComputedStyle(
      document.documentElement
    ).getPropertyValue("--greenBackColor");
  } else if (color === 4) {
    selectedColor = getComputedStyle(document.documentElement).getPropertyValue(
      "--grayColor"
    );
    selectedBackColor = getComputedStyle(
      document.documentElement
    ).getPropertyValue("--grayBackColor");
  }

  document.documentElement.style.setProperty(
    "--todoBackgroundColor",
    selectedColor
  );
  document.documentElement.style.setProperty(
    "--backgroundColor",
    selectedBackColor
  );

  const onChangeColor = (colorID) => {
    var selectedColor = null;
    var selectedBackColor = null;
    if (colorID === 1) {
      selectedColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--blueColor");
      selectedBackColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--blueBackColor");
    } else if (colorID === 2) {
      selectedColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--purpleColor");
      selectedBackColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--purpleBackColor");
    } else if (colorID === 3) {
      selectedColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--greenColor");
      selectedBackColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--greenBackColor");
    } else if (colorID === 4) {
      selectedColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--grayColor");
      selectedBackColor = getComputedStyle(
        document.documentElement
      ).getPropertyValue("--grayBackColor");
    }

    document.documentElement.style.setProperty(
      "--todoBackgroundColor",
      selectedColor
    );
    document.documentElement.style.setProperty(
      "--backgroundColor",
      selectedBackColor
    );
  };
  return (
    <table className="ColorTable">
      <tbody>
        <tr>
          <td>
            <div
              className="blueTemplate"
              onClick={(e) => onChangeColor(1)}
            ></div>
          </td>
          <td>
            <div
              className="purpleTemplate"
              onClick={(e) => onChangeColor(2)}
            ></div>
          </td>
          <td>
            <div
              className="greenTemplate"
              onClick={(e) => onChangeColor(3)}
            ></div>
          </td>
          <td>
            <div
              className="grayTemplate"
              onClick={(e) => onChangeColor(4)}
            ></div>
          </td>
        </tr>
      </tbody>
    </table>
  );
};

export default ColorPick;
