import React from "react";
import { useState } from "react";
import ReactDatePicker from "react-datepicker";
import "./DatePick.css";
import moment from "moment";
require("react-datepicker/dist/react-datepicker.css");

const DatePick = ({ todo, onSettingDate }) => {
  const [startDate, setStartDate] = useState(
    todo.dday ? new Date(todo.dday) : null
  );

  const onClickDate = (date) => {
    setStartDate(date);
    if (date != null) {
      const dday = moment(date).format("YYYY / MM / DD");
      onSettingDate(todo.id, dday);
    } else {
      onSettingDate(todo.id, date);
    }
  };

  const onDeleteDDay = () => {
    if (startDate != null) {
      if (window.confirm("해당 마감일을 없애시겠습니까?")) {
        onClickDate(null);
      }
    }
  };

  const CustomInput = ({ value, onClick }) => (
    <button className="datePickBtn" onClick={onClick} click={onDeleteDDay}>
      {value}
    </button>
  );

  return (
    <div>
      <ReactDatePicker
        placeholderText="dsaf"
        dateFormat="yyyy / MM / dd"
        selected={startDate}
        onChange={(date) => onClickDate(date)}
        customInput={<CustomInput />}
        todayButton="Today"
      />
    </div>
  );
};

export default DatePick;
