import React from "react";
import { IoMdCheckmark, IoMdClose } from "react-icons/io";
import { BiEditAlt } from "react-icons/bi";
import "./TodoItem.css";
import { useState } from "react";

//할일 하나
const TodoItem = ({ todo, onToggle, onRemove, onEdit }) => {
  //const visible = false;
  const [visible, setVisible] = useState(false);

  const onEditClick = () => {
    setVisible(true);
  };
  const onCancel = () => {
    setVisible(false);
  };
  const onConfirm = () => {
    setVisible(false);
    onEdit(todo.id);
  };
  const showText = todo.text.replace("/n", "<br>");
  return (
    <div
      key={todo.id}
      className="listItem"
      // onDoubleClick={() => onEdit(todo.id)}
    >
      {todo.checked ? (
        <div className="todoItemChecked">{todo.text}</div>
      ) : (
        <div className="todoItem">{todo.text}</div>
      )}
      <div className="btnsDiv">
        <table>
          <td>
            <IoMdCheckmark
              size="30"
              className="checkBtn"
              onClick={() => onToggle(todo.id)}
            />
          </td>
          <td>
            <BiEditAlt
              size="30"
              className="editBtn"
              onClick={() => onEdit(todo.id)}
              //onClick={onEditClick}
            />
          </td>
          <td>
            <IoMdClose
              size="30"
              className="deleteBtn"
              onClick={() => onRemove(todo.id)}
            />
          </td>
        </table>
      </div>
      {/* <AskModal
        visible={visible}
        title="수정"
        description={todo.text}
        confirmText="EDIT"
        onConfirm={onConfirm}
        onCancel={onCancel}
      /> */}
    </div>
  );
};

export default TodoItem;
