import axios from "axios";
import todos from "../modules/todos";

const TODO_API_BASE_URL = "http://localhost:8080/todos";

export const getTodos = () => {
  axios.get(TODO_API_BASE_URL);
};

export const insertTodo = (todo) => {
  axios.post(TODO_API_BASE_URL, todo);
};

export const updateTodo = (todo) => {
  axios.put(TODO_API_BASE_URL + "/" + todo.id, todo);
};

export const deleteTodo = (id) => {
  axios.delete(TODO_API_BASE_URL + "/" + id);
};
