import axios from "axios";
import todos from "../modules/todos";

const TODO_API_BASE_URL = "localhost:8080/todos";

export const getTodos = () => {
  axios.get(TODO_API_BASE_URL);
  //console.log(axios.get("https://jsonplaceholder.typicode.com/todos"));
  //const a = axios.get("https://jsonplaceholder.typicode.com/todos");
};

export const insertTodos = (todo) => {
  axios.post(TODO_API_BASE_URL, todo);
};

export const updateTodos = (todo) => {
  axios.put(TODO_API_BASE_URL + "/" + todo.id, todo);
};

export const deleteTodos = (id) => {
  axios.delete(TODO_API_BASE_URL + "/" + todo.id);
};
