      //     window.history.go(0);
      //   })
      //   .catch(function (error) {
      //     console.log(error);
      //   });
      //비동기 처리 함수
      const rmcfn = async () => {
        try {
          //검색한 후, 기다렸다가 검색 결과를 response에 담음
          const response = await deleteCheckedTodo();
          console.log(response);
        } catch (e) {
          console.log(e);
        }
      };
      rmcfn();
    }
  };
  return (
    <button className="Todo-Delete-Btn" onClick={onClickDeleteChecked}>
      완료Todo삭제
    </button>
  );
};

export default DeleteChecked;
